import React from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import { useQuery } from 'react-query';
import Layout from './components/Layout';
import LoginPage from './pages/LoginPage';
import DashboardPage from './pages/DashboardPage';
import SearchCIGPage from './pages/SearchCIGPage';
import TableDetailPage from './pages/TableDetailPage';
import { authAPI } from './services/api';

function App() {
  const hasToken = authAPI.isAuthenticated();
  
  const { data: user, isLoading, error } = useQuery(
    'currentUser',
    authAPI.getCurrentUser,
    {
      enabled: hasToken,
      retry: false,
    }
  );

  // Show loading only when we have a token but are still fetching user data
  if (hasToken && isLoading) {
    return (
      <div className="min-h-screen flex items-center justify-center">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  // If there's an error fetching user data but we have a token, still consider authenticated
  // This prevents users from being stuck at login due to API issues
  const isAuthenticated = hasToken && (user || error);

  return (
    <div className="App">
      <Routes>
        <Route
          path="/login"
          element={
            isAuthenticated ? <Navigate to="/" replace /> : <LoginPage />
          }
        />
        <Route
          path="/"
          element={
            isAuthenticated ? (
              <Layout>
                <DashboardPage />
              </Layout>
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />
        <Route
          path="/search"
          element={
            isAuthenticated ? (
              <Layout>
                <SearchCIGPage />
              </Layout>
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />
        <Route
          path="/table/:tableName"
          element={
            isAuthenticated ? (
              <Layout>
                <TableDetailPage />
              </Layout>
            ) : (
              <Navigate to="/login" replace />
            )
          }
        />
        <Route path="*" element={<Navigate to="/" replace />} />
      </Routes>
    </div>
  );
}

export default App;
