import { useState } from 'react';
import { Link } from 'react-router-dom';
import { useQuery, useMutation } from 'react-query';
import { Database, Table, ArrowRight, Search, Building2, Clock, CheckCircle } from 'lucide-react';
import { tablesAPI, searchAPI } from '../services/api';
import SearchByCIG from '../components/SearchByCIG';
import SearchResultsDisplay from '../components/SearchResultsDisplay';
import AdvancedSearch from '../components/AdvancedSearch';
import CompanySearchResults from '../components/CompanySearchResults';
import SimpleCompanyResults from '../components/SimpleCompanyResults';

const DashboardPage = () => {
  const [searchType, setSearchType] = useState<'cig' | 'company' | 'company-stream'>('cig');
  const [searchResults, setSearchResults] = useState<any>(null);
  const [companyResults, setCompanyResults] = useState<any>(null);
  
  // Streaming search state
  const [streamingResults, setStreamingResults] = useState<any[]>([]);
  const [streamingProgress, setStreamingProgress] = useState<{
    currentTable: string;
    tableIndex: number;
    totalTables: number;
    is_priority: boolean;
    priority_tables: number;
  } | null>(null);
  const [streamingStatus, setStreamingStatus] = useState<string>('');
  const [totalMatches, setTotalMatches] = useState(0);

  const {
    data: tables,
    isLoading: tablesLoading,
    error: tablesError,
  } = useQuery('tables', tablesAPI.getTables);

  const cigSearchMutation = useMutation(searchAPI.searchByCIG, {
    onSuccess: (data) => {
      setSearchResults(data);
      setCompanyResults(null);
    },
  });

  const companySearchMutation = useMutation(
    async (companyName: string) => {
      // Use streaming search instead of normal search
      return new Promise((resolve, reject) => {
        const allResults: any[] = [];
        let finalData: any = null;

        const handleProgress = (data: any) => {
          console.log('Streaming progress:', data);
          
          if (data.type === 'table_result') {
            allResults.push(data);
            setTotalMatches((prev: number) => prev + data.matches);
            
            // Update results immediately as they arrive
            const currentResults = {
              company_name: companyName,
              found: allResults.length > 0,
              total_matches: allResults.reduce((sum, result) => sum + result.matches, 0),
              tables_searched: allResults.length,
              results_by_table: allResults,
              search_timestamp: new Date().toISOString()
            };
            
            setCompanyResults(currentResults);
            setSearchResults(null);
          }
          
          if (data.type === 'progress') {
            setStreamingProgress({
              currentTable: data.current_table,
              tableIndex: data.table_index,
              totalTables: data.total_tables,
              is_priority: data.is_priority,
              priority_tables: data.priority_tables
            });
          }
          
          if (data.type === 'status') {
            setStreamingStatus(data.message);
          }
        };

        const handleComplete = (data: any) => {
          console.log('Streaming completed:', data);
          finalData = {
            company_name: data.company_name,
            found: data.found,
            total_matches: data.total_matches,
            tables_searched: data.tables_searched,
            results_by_table: allResults,
            search_timestamp: data.search_timestamp
          };
          
          // Final update with complete data
          setCompanyResults(finalData);
          setStreamingStatus('Search completed!');
          resolve(finalData);
        };

        const handleError = (error: any) => {
          console.error('Streaming error:', error);
          reject(error);
        };

        // Start streaming search
        setStreamingResults([]);
        setStreamingProgress(null);
        setStreamingStatus('Starting search...');
        setTotalMatches(0);
        setCompanyResults(null); // Clear previous results
        setSearchResults(null);

        searchAPI.searchByCompanyStream(
          companyName,
          handleProgress,
          handleComplete,
          handleError
        ).catch(reject);
      });
    },
    {
      onSuccess: (data) => {
        console.log('Company search completed:', data);
        setStreamingStatus('Search completed!');
      },
      onError: (error: any) => {
        console.error('Company search error:', error);
        setStreamingStatus(`Error: ${error.message || 'Unknown error'}`);
      },
    }
  );

  const handleCIGSearch = (cig: string) => {
    cigSearchMutation.mutate(cig);
  };

  const handleCompanySearch = (companyName: string) => {
    companySearchMutation.mutate(companyName);
  };



  // Helper functions for table display
  const getTableDisplayName = (tableName: string): string => {
    return tableName.charAt(0).toUpperCase() + tableName.slice(1).replace(/_/g, ' ');
  };

  const getTableIcon = (tableName: string) => {
    return <Table className="h-4 w-4" />;
  };

  if (tablesLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-gray-600"></div>
      </div>
    );
  }

  if (tablesError) {
    return (
      <div className="text-center py-12">
        <div className="text-red-500 text-lg">Error loading tables</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center space-x-3">
        <Database className="h-8 w-8 text-black" />
        <div>
          <h1 className="text-3xl font-bold text-black">DBSee Dashboard</h1>
          <p className="text-gray-700">
            Ricerca per CIG, aziende e esplora le tabelle del database
          </p>
        </div>
      </div>

      {/* Search Type Toggle */}
      <div className="bg-white rounded-lg border border-gray-500 shadow-lg p-4">
        <div className="flex items-center space-x-4">
          <span className="text-sm font-medium text-gray-700">Tipo di ricerca:</span>
          <div className="flex items-center space-x-2">
            <button
              onClick={() => setSearchType('cig')}
              className={`inline-flex items-center px-4 py-2 text-sm font-medium rounded-lg transition-colors ${
                searchType === 'cig'
                  ? 'bg-black text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              <Search className="h-4 w-4 mr-2" />
              Ricerca per CIG
            </button>
            <button
              onClick={() => setSearchType('company')}
              className={`inline-flex items-center px-4 py-2 text-sm font-medium rounded-lg transition-colors ${
                searchType === 'company'
                  ? 'bg-black text-white'
                  : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
              }`}
            >
              <Building2 className="h-4 w-4 mr-2" />
              Ricerca per Azienda
            </button>
          </div>
        </div>
      </div>

      {/* Search Components */}
      {searchType === 'cig' ? (
        <SearchByCIG
          onSearch={handleCIGSearch}
          isLoading={cigSearchMutation.isLoading}
        />
      ) : (
        <AdvancedSearch
          onSearch={handleCompanySearch}
          isLoading={companySearchMutation.isLoading}
        />
      )}

      {/* Streaming Progress Display */}
      {companySearchMutation.isLoading && streamingProgress && (
        <div className="bg-white rounded-lg border border-gray-500 shadow-lg p-6">
          <div className="flex items-center space-x-3 mb-4">
            <Clock className="h-5 w-5 text-blue-500 animate-spin" />
            <h3 className="text-lg font-medium text-black">Ricerca in Corso (Streaming)</h3>
          </div>
          
          <div className="space-y-3">
            <div className="text-sm text-gray-600">{streamingStatus}</div>
            
            <div className="space-y-2">
              <div className="flex justify-between text-sm">
                <span>
                  Tabella corrente: <strong>{streamingProgress.currentTable}</strong>
                  {streamingProgress.is_priority && (
                    <span className="ml-2 inline-flex items-center px-1.5 py-0.5 rounded text-xs font-medium bg-yellow-100 text-yellow-800">
                      PRIORITÀ
                    </span>
                  )}
                </span>
                <span>{streamingProgress.tableIndex} / {streamingProgress.totalTables}</span>
              </div>
              <div className="w-full bg-gray-200 rounded-full h-2">
                <div 
                  className={`h-2 rounded-full transition-all duration-300 ${
                    streamingProgress.is_priority ? 'bg-yellow-500' : 'bg-black'
                  }`}
                  style={{ width: `${(streamingProgress.tableIndex / streamingProgress.totalTables) * 100}%` }}
                ></div>
              </div>
              
              {/* Add priority tables indicator */}
              {streamingProgress.priority_tables && (
                <div className="text-xs text-gray-500">
                  Tabelle prioritarie: {Math.min(streamingProgress.tableIndex, streamingProgress.priority_tables)} / {streamingProgress.priority_tables}
                </div>
              )}
            </div>
            
            <div className="text-sm text-gray-600">
              Risultati trovati finora: <strong>{totalMatches}</strong>
            </div>

            {streamingResults.length > 0 && (
              <div className="mt-4">
                <h4 className="text-sm font-medium text-gray-700 mb-2">Tabelle con risultati:</h4>
                <div className="flex flex-wrap gap-2">
                  {streamingResults.map((result, index) => (
                    <span 
                      key={index}
                      className="inline-flex items-center px-2 py-1 rounded-full text-xs bg-green-100 text-green-800"
                    >
                      <CheckCircle className="h-3 w-3 mr-1" />
                      {result.table_name} ({result.matches})
                    </span>
                  ))}
                </div>
              </div>
            )}
          </div>
        </div>
      )}

      {/* Search Results */}
      {searchResults && (
        <SearchResultsDisplay
          results={searchResults}
          getTableDisplayName={getTableDisplayName}
          getTableIcon={getTableIcon}
        />
      )}

      {companyResults && (
        <div>
          <SimpleCompanyResults 
            results={companyResults} 
            onCigSearch={handleCIGSearch}
          />
          <CompanySearchResults
            results={companyResults}
            getTableDisplayName={getTableDisplayName}
            getTableIcon={getTableIcon}
          />
        </div>
      )}

      {/* Tables Section */}
      <div className="border-t border-gray-300 pt-6">
        <h2 className="text-2xl font-bold text-black mb-4">Tabelle Database</h2>
        
        {tables && tables.length > 0 ? (
          <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
            {tables.map((tableName) => (
              <Link
                key={tableName}
                to={`/table/${tableName}`}
                className="card p-6 hover:shadow-lg transition-shadow duration-200 group"
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="flex-shrink-0">
                      <Table className="h-8 w-8 text-black" />
                    </div>
                    <div>
                      <h3 className="text-lg font-medium text-black group-hover:text-gray-700">
                        {tableName}
                      </h3>
                      <p className="text-sm text-gray-600">
                        Click to explore data
                      </p>
                    </div>
                  </div>
                  <ArrowRight className="h-5 w-5 text-gray-500 group-hover:text-black" />
                </div>
              </Link>
            ))}
          </div>
        ) : (
          <div className="text-center py-12">
            <Database className="mx-auto h-12 w-12 text-gray-400" />
            <h3 className="mt-2 text-sm font-medium text-gray-900">
              No tables found
            </h3>
            <p className="mt-1 text-sm text-gray-500">
              No tables were found in the database.
            </p>
          </div>
        )}
      </div>
    </div>
  );
};

export default DashboardPage;
