import { Link } from 'react-router-dom';
import { useQuery } from 'react-query';
import { Database, Table, ArrowRight } from 'lucide-react';
import { tablesAPI } from '@/services/api';

const DashboardPage = () => {
  const {
    data: tables,
    isLoading,
    error,
  } = useQuery('tables', tablesAPI.getTables);

  if (isLoading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-32 w-32 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-12">
        <div className="text-red-500 text-lg">Error loading tables</div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex items-center space-x-3">
        <Database className="h-8 w-8 text-primary-600" />
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Database Tables</h1>
          <p className="text-gray-600">
            Explore and query your database tables
          </p>
        </div>
      </div>

      {tables && tables.length > 0 ? (
        <div className="grid grid-cols-1 gap-6 sm:grid-cols-2 lg:grid-cols-3">
          {tables.map((tableName) => (
            <Link
              key={tableName}
              to={`/table/${tableName}`}
              className="card p-6 hover:shadow-lg transition-shadow duration-200 group"
            >
              <div className="flex items-center justify-between">
                <div className="flex items-center space-x-3">
                  <div className="flex-shrink-0">
                    <Table className="h-8 w-8 text-primary-600" />
                  </div>
                  <div>
                    <h3 className="text-lg font-medium text-gray-900 group-hover:text-primary-600">
                      {tableName}
                    </h3>
                    <p className="text-sm text-gray-500">
                      Click to explore data
                    </p>
                  </div>
                </div>
                <ArrowRight className="h-5 w-5 text-gray-400 group-hover:text-primary-600" />
              </div>
            </Link>
          ))}
        </div>
      ) : (
        <div className="text-center py-12">
          <Database className="mx-auto h-12 w-12 text-gray-400" />
          <h3 className="mt-2 text-sm font-medium text-gray-900">
            No tables found
          </h3>
          <p className="mt-1 text-sm text-gray-500">
            No tables were found in the database.
          </p>
        </div>
      )}
    </div>
  );
};

export default DashboardPage;
