import { useState } from 'react';
import { ChevronDown, ChevronRight, Eye, Table } from 'lucide-react';
import { CIGSearchResult } from '../types/api';

interface SearchResultsDisplayProps {
  results: CIGSearchResult;
  getTableDisplayName: (tableName: string) => string;
  getTableIcon: (tableName: string) => JSX.Element;
}

const SearchResultsDisplay = ({ 
  results, 
  getTableDisplayName, 
  getTableIcon 
}: SearchResultsDisplayProps) => {
  const [expandedSections, setExpandedSections] = useState<Set<string>>(new Set());
  const [selectedRecord, setSelectedRecord] = useState<{
    tableName: string;
    record: Record<string, any>;
  } | null>(null);
  const [showMergedView, setShowMergedView] = useState(true);

  const toggleSection = (tableName: string) => {
    const newExpanded = new Set(expandedSections);
    if (newExpanded.has(tableName)) {
      newExpanded.delete(tableName);
    } else {
      newExpanded.add(tableName);
    }
    setExpandedSections(newExpanded);
  };

  const formatCellValue = (value: any): string => {
    if (value === null || value === undefined) {
      return '-';
    }
    if (typeof value === 'boolean') {
      return value ? 'Sì' : 'No';
    }
    if (typeof value === 'number') {
      // Format currency if the field name suggests it's a monetary value
      if (typeof value === 'number' && value > 1000) {
        return new Intl.NumberFormat('it-IT', {
          style: 'decimal',
          minimumFractionDigits: 2,
          maximumFractionDigits: 2
        }).format(value);
      }
    }
    return String(value);
  };

  const getKeyColumns = (tableName: string, columns: string[]): string[] => {
    // Define which columns are most important for each table type
    const keyColumnsByTable: Record<string, string[]> = {
      'contratti': ['cig', 'oggetto', 'importo', 'data_stipula', 'fornitore'],
      'fornitori': ['cig', 'denominazione', 'codice_fiscale', 'partita_iva'],
      'pagamenti': ['cig', 'importo', 'data_pagamento', 'causale'],
      'gare': ['cig', 'oggetto', 'importo', 'data_pubblicazione'],
      'fatture': ['cig', 'numero', 'importo', 'data_emissione'],
    };

    const keyColumns = keyColumnsByTable[tableName.toLowerCase()];
    if (keyColumns) {
      return keyColumns.filter(col => columns.includes(col));
    }

    // Fallback: show first 5 columns
    return columns.slice(0, 5);
  };

  // Create merged table data
  const getMergedData = () => {
    const merged: Array<{
      record: Record<string, any>;
      tableName: string;
      index: number;
    }> = [];

    Object.entries(results.sections).forEach(([tableName, section]) => {
      section.data.forEach((record, index) => {
        merged.push({ record, tableName, index });
      });
    });

    return merged;
  };

  const mergedData = getMergedData();

  return (
    <div className="space-y-6">
      {/* Toggle buttons */}
      <div className="flex gap-2 justify-center">
        <button
          onClick={() => setShowMergedView(true)}
          className={`px-4 py-2 rounded-lg font-medium transition-colors ${
            showMergedView 
              ? 'bg-blue-600 text-white' 
              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          Vista Unificata
        </button>
        <button
          onClick={() => setShowMergedView(false)}
          className={`px-4 py-2 rounded-lg font-medium transition-colors ${
            !showMergedView 
              ? 'bg-blue-600 text-white' 
              : 'bg-gray-200 text-gray-700 hover:bg-gray-300'
          }`}
        >
          Vista per Tabelle
        </button>
      </div>

      {showMergedView ? (
        /* Merged View */
        <div className="border border-gray-200 rounded-lg overflow-hidden">
          <div className="bg-gray-50 px-4 py-3">
            <div className="flex items-center space-x-3">
              <Table className="h-6 w-6 text-blue-600" />
              <h3 className="text-lg font-semibold text-gray-900">
                Tutti i Risultati per CIG: {results.cig}
              </h3>
              <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                {results.total_records} record totali
              </span>
            </div>
          </div>
          
          <div className="p-4">
            <div className="overflow-x-auto">
              <table className="min-w-full divide-y divide-gray-300">
                <thead className="bg-gray-50">
                  <tr>
                    <th className="px-2 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      CIG
                    </th>
                    <th className="px-2 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Fonte
                    </th>
                    <th className="px-2 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Oggetto/Descrizione
                    </th>
                    <th className="px-2 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Fornitore/Denominazione
                    </th>
                    <th className="px-2 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Codice Fiscale
                    </th>
                    <th className="px-2 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Importo €
                    </th>
                    <th className="px-2 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Data
                    </th>
                    <th className="px-2 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Stato/Categoria
                    </th>
                    <th className="px-2 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                      Note/Riferimenti
                    </th>
                  </tr>
                </thead>
                <tbody className="bg-white divide-y divide-gray-200">
                  {mergedData.map((item, index) => {
                    const record = item.record;
                    
                    // Extract all relevant fields
                    const oggetto = record.oggetto || record.denominazione_oggetto || record.descrizione || record.causale || '-';
                    const fornitore = record.fornitore || record.denominazione || record.ragione_sociale || record.nome || '-';
                    const codiceFiscale = record.codice_fiscale || record.partita_iva || record.cf_piva || '-';
                    const importo = record.importo || record.valore || record.importo_pagamento || record.prezzo || '-';
                    const data = record.data_stipula || record.data_pagamento || record.data_pubblicazione || record.data_emissione || record.data_inizio || record.data_fine || '-';
                    const stato = record.stato || record.categoria || record.tipo || record.fase || '-';
                    const note = record.note || record.numero || record.id_aggiudicazione || record.batch_id || record.source_file || '-';
                    
                    // Determine row color based on table type
                    let bgColorClass = '';
                    if (item.tableName.toLowerCase().includes('aggiudicat')) bgColorClass = 'bg-green-50';
                    else if (item.tableName.toLowerCase().includes('contratt')) bgColorClass = 'bg-blue-50';
                    else if (item.tableName.toLowerCase().includes('pagament')) bgColorClass = 'bg-yellow-50';
                    else if (item.tableName.toLowerCase().includes('gare') || item.tableName.toLowerCase().includes('cig')) bgColorClass = 'bg-purple-50';
                    else if (item.tableName.toLowerCase().includes('categorie')) bgColorClass = 'bg-indigo-50';
                    else if (item.tableName.toLowerCase().includes('lavora')) bgColorClass = 'bg-orange-50';
                    
                    return (
                      <tr key={`${item.tableName}-${index}`} className={`hover:bg-gray-100 ${bgColorClass}`}>
                        <td className="px-2 py-2 whitespace-nowrap text-sm font-medium text-gray-900">
                          {record.cig}
                        </td>
                        <td className="px-2 py-2 whitespace-nowrap text-sm text-gray-900">
                          <span className={`inline-flex items-center px-2 py-1 rounded-full text-xs font-medium ${
                            item.tableName.toLowerCase().includes('aggiudicat') ? 'bg-green-100 text-green-800' :
                            item.tableName.toLowerCase().includes('contratt') ? 'bg-blue-100 text-blue-800' :
                            item.tableName.toLowerCase().includes('pagament') ? 'bg-yellow-100 text-yellow-800' :
                            item.tableName.toLowerCase().includes('gare') || item.tableName.toLowerCase().includes('cig') ? 'bg-purple-100 text-purple-800' :
                            item.tableName.toLowerCase().includes('categorie') ? 'bg-indigo-100 text-indigo-800' :
                            item.tableName.toLowerCase().includes('lavora') ? 'bg-orange-100 text-orange-800' :
                            'bg-gray-100 text-gray-800'
                          }`}>
                            {getTableDisplayName(item.tableName)}
                          </span>
                        </td>
                        <td className="px-2 py-2 text-sm text-gray-900 max-w-xs">
                          <div className="truncate" title={formatCellValue(oggetto)}>
                            {formatCellValue(oggetto)}
                          </div>
                        </td>
                        <td className="px-2 py-2 text-sm text-gray-900 max-w-xs">
                          <div className="truncate" title={formatCellValue(fornitore)}>
                            {formatCellValue(fornitore)}
                          </div>
                        </td>
                        <td className="px-2 py-2 whitespace-nowrap text-sm text-gray-900">
                          {formatCellValue(codiceFiscale)}
                        </td>
                        <td className="px-2 py-2 whitespace-nowrap text-sm text-gray-900 font-medium">
                          {importo !== '-' && typeof importo === 'number' ? 
                            `€ ${new Intl.NumberFormat('it-IT').format(importo)}` : 
                            formatCellValue(importo)
                          }
                        </td>
                        <td className="px-2 py-2 whitespace-nowrap text-sm text-gray-900">
                          {formatCellValue(data)}
                        </td>
                        <td className="px-2 py-2 text-sm text-gray-900 max-w-xs">
                          <div className="truncate" title={formatCellValue(stato)}>
                            {formatCellValue(stato)}
                          </div>
                        </td>
                        <td className="px-2 py-2 text-sm text-gray-600 max-w-xs">
                          <div className="truncate" title={formatCellValue(note)}>
                            {formatCellValue(note)}
                          </div>
                        </td>
                      </tr>
                    );
                  })}
                </tbody>
              </table>
            </div>
            
            {/* Summary info */}
            <div className="mt-4 p-3 bg-gray-50 rounded-lg">
              <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                <div>
                  <span className="font-medium text-gray-700">Totale Record:</span>
                  <span className="ml-2 text-gray-900">{results.total_records}</span>
                </div>
                <div>
                  <span className="font-medium text-gray-700">Tabelle Coinvolte:</span>
                  <span className="ml-2 text-gray-900">{results.tables_with_results}</span>
                </div>
                <div>
                  <span className="font-medium text-gray-700">CIG Cercato:</span>
                  <span className="ml-2 text-gray-900 font-mono">{results.cig}</span>
                </div>
                <div>
                  <span className="font-medium text-gray-700">Ultima Ricerca:</span>
                  <span className="ml-2 text-gray-900">{new Date().toLocaleString('it-IT')}</span>
                </div>
              </div>
            </div>
          </div>
        </div>
      ) : (
        /* Table-by-table View */
        <div className="space-y-4">
          {Object.entries(results.sections).map(([tableName, section]) => (
            <div key={tableName} className="border border-gray-200 rounded-lg overflow-hidden">
              {/* Section Header */}
              <div 
                className="bg-gray-50 px-4 py-3 cursor-pointer hover:bg-gray-100 transition-colors"
                onClick={() => toggleSection(tableName)}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="text-primary-600">
                      {getTableIcon(tableName)}
                    </div>
                    <h3 className="text-lg font-semibold text-gray-900">
                      {getTableDisplayName(tableName)}
                    </h3>
                    <span className="inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-primary-100 text-primary-800">
                      {section.count} record
                    </span>
                  </div>
                  <div className="text-gray-400">
                    {expandedSections.has(tableName) ? (
                      <ChevronDown className="h-5 w-5" />
                    ) : (
                      <ChevronRight className="h-5 w-5" />
                    )}
                  </div>
                </div>
              </div>

              {/* Section Content */}
              {expandedSections.has(tableName) && (
                <div className="p-4">
                  {section.error ? (
                    <div className="text-red-600 text-sm">
                      Errore nel caricamento dei dati: {section.error}
                    </div>
                  ) : (
                    <>
                      {/* Quick preview table */}
                      <div className="overflow-x-auto">
                        <table className="min-w-full divide-y divide-gray-300">
                          <thead className="bg-gray-50">
                            <tr>
                              {getKeyColumns(tableName, section.columns).map((column) => (
                                <th
                                  key={column}
                                  className="px-3 py-2 text-left text-xs font-medium text-gray-500 uppercase tracking-wider"
                                >
                                  {column.replace(/_/g, ' ')}
                                </th>
                              ))}
                              <th className="px-3 py-2 text-right text-xs font-medium text-gray-500 uppercase tracking-wider">
                                Azioni
                              </th>
                            </tr>
                          </thead>
                          <tbody className="bg-white divide-y divide-gray-200">
                            {section.data.slice(0, 5).map((record, index) => (
                              <tr key={index} className="hover:bg-gray-50">
                                {getKeyColumns(tableName, section.columns).map((column) => (
                                  <td key={column} className="px-3 py-2 whitespace-nowrap text-sm text-gray-900">
                                    {formatCellValue(record[column])}
                                  </td>
                                ))}
                                <td className="px-3 py-2 whitespace-nowrap text-right text-sm font-medium">
                                  <button
                                    onClick={() => setSelectedRecord({ tableName, record })}
                                    className="text-primary-600 hover:text-primary-900"
                                    title="Visualizza dettagli completi"
                                  >
                                    <Eye className="h-4 w-4" />
                                  </button>
                                </td>
                              </tr>
                            ))}
                          </tbody>
                        </table>
                      </div>

                      {section.count > 5 && (
                        <div className="mt-3 text-sm text-gray-600 text-center">
                          Mostrati 5 di {section.count} record.
                          <button
                            onClick={() => {
                              // Here you could implement pagination or show all records
                              console.log(`Show all ${section.count} records for ${tableName}`);
                            }}
                            className="ml-2 text-primary-600 hover:text-primary-800 font-medium"
                          >
                            Mostra tutti
                          </button>
                        </div>
                      )}
                    </>
                  )}
                </div>
              )}
            </div>
          ))}
        </div>
      )}

      {/* Record Detail Modal */}
      {selectedRecord && (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 overflow-y-auto h-full w-full z-50">
          <div className="relative top-20 mx-auto p-5 border w-11/12 max-w-4xl shadow-lg rounded-md bg-white">
            <div className="mt-3">
              <div className="flex items-center justify-between mb-4">
                <h3 className="text-lg font-semibold text-gray-900">
                  Dettagli - {getTableDisplayName(selectedRecord.tableName)}
                </h3>
                <button
                  onClick={() => setSelectedRecord(null)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  ✕
                </button>
              </div>
              
              <div className="grid grid-cols-1 md:grid-cols-2 gap-4 max-h-96 overflow-y-auto">
                {Object.entries(selectedRecord.record).map(([key, value]) => (
                  <div key={key} className="border-b border-gray-200 pb-2">
                    <dt className="text-sm font-medium text-gray-500">
                      {key.replace(/_/g, ' ').toUpperCase()}
                    </dt>
                    <dd className="mt-1 text-sm text-gray-900">
                      {formatCellValue(value)}
                    </dd>
                  </div>
                ))}
              </div>
              
              <div className="mt-4 flex justify-end">
                <button
                  onClick={() => setSelectedRecord(null)}
                  className="btn-secondary"
                >
                  Chiudi
                </button>
              </div>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default SearchResultsDisplay;
